mapper.select:
SELECT COUNT(1)
FROM TB_REPORT
WHERE REPORT_TARGET_TYPE = #{targetType}
AND REPORT_TARGET_ID = #{targetId}
AND USER_ID = #{userId}

SELECT *
FROM TB_REPORT
WHERE REPORT_TARGET_TYPE = #{targetType}
AND REPORT_TARGET_ID = #{targetId}
ORDER BY REPORT_DATE DESC

mapper.update:
UPDATE TB_REPORT
SET
REPORT_STATUS = #{reportStatus},
UPDATED_DATE = SYSDATE,
UPDATED_ID = #{updatedId}
WHERE REPORT_ID = #{reportId}
